document.addEventListener('DOMContentLoaded', function() {
    var whatsappBtn = document.getElementById('whatsapp-btn');
    var whatsappPopup = document.getElementById('whatsapp-popup');
    var isPopupOpen = false; // Track the popup state

    // Toggle the popup on button click
    whatsappBtn.addEventListener('click', function(event) {
        event.stopPropagation(); // Prevent the click event from reaching the window
        isPopupOpen = !isPopupOpen; // Toggle the state

        if (isPopupOpen) {
            whatsappPopup.style.display = 'block';
        } else {
            whatsappPopup.style.display = 'none';
        }
    });

    // Close the popup when clicking outside
    window.addEventListener('click', function() {
        if (isPopupOpen) {
            whatsappPopup.style.display = 'none';
            isPopupOpen = false; // Reset the state
        }
    });

    // Prevent the popup from closing if clicking inside it
    whatsappPopup.addEventListener('click', function(event) {
        event.stopPropagation();
    });
});

